BasicProperties.ActionCode:
f_setPVSystemCompanies( zero_Interface.energyModel.UtilityConnections.findAll( x -> true), sl_rooftopPVCompanies_pct.getValue(), sl_rooftopPVCompanies_pct );

if( zero_Interface.rb_buildingColors.getValue() == 2){
	zero_Interface.rb_buildingColors.setValue(2, true);
}

f_setPVSystemHouses( zero_Interface.energyModel.Houses.findAll( x -> true), sl_rooftopPVHouses_pct.getValue() );

if( zero_Interface.rb_buildingColors.getValue() == 2){
	zero_Interface.rb_buildingColors.setValue(2, true);
}

f_setPVSystemHouses( findAll(zero_Interface.energyModel.Houses, x -> true), sl_householdPVResidentialArea_pct.getValue() );
if( zero_Interface.rb_buildingColors.getValue() == 2){
	zero_Interface.rb_buildingColors.setValue(2, true); //renew the PV view of houses
}

// Also updates the home batteries
f_setResidentialBatteries( sl_householdBatteriesResidentialArea_pct.getValue() );

switch (chargingMethodResidentialArea.getValue()){
	case 0:
		gr_settingsV1G.setVisible(false);
		gr_settingsV2G.setVisible(false);

		//zero_Interface.energyModel.v_smartChargingActive = false;
		//zero_Interface.energyModel.v_V2GProbability = 0;
		for( GCHouse house : zero_Interface.energyModel.Houses ){
			house.p_chargingAttitudeVehicles = OL_ChargingAttitude.SIMPLE;
		}
		break;
	case 1:
		gr_settingsV1G.setVisible(true);
		gr_settingsV2G.setVisible(false);
		//zero_Interface.energyModel.v_smartChargingActive = true;
		for( GCHouse house : zero_Interface.energyModel.Houses ){
			house.p_chargingAttitudeVehicles = OL_ChargingAttitude.V1G;
		}
		//zero_Interface.energyModel.v_V2GProbability = 0;
		break;
	case 2:
		gr_settingsV1G.setVisible(false);
		gr_settingsV2G.setVisible(true);
		for( GCHouse house : zero_Interface.energyModel.Houses ){
			house.p_chargingAttitudeVehicles = OL_ChargingAttitude.V2G; 
		}
		//zero_Interface.energyModel.v_smartChargingActive = true;
		//zero_Interface.energyModel.v_V2GProbability = 1;
		break;
}

// Update variable to change to custom scenario
if(!zero_Interface.b_runningMainInterfaceScenarios){
	zero_Interface.b_changeToCustomScenario = true;
}

zero_Interface.f_resetSettings();

